import React, { useState } from 'react'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'
import Button from '@material-ui/core/Button'

import Card from 'components/Card'

export default function App() {
  const [selectedPasta, setSelectedPasta] = useState('')

  console.log(selectedPasta)

  function handleOptionChange(e) {
    setSelectedPasta(e.target.value)
  }

  return (
    <div>
      <label>
        <input
          width={300}
          height={300}
          type="radio"
          value="option1"
          checked={selectedPasta === 'option1'}
          onChange={handleOptionChange}
        />
        Option 1
      </label>
      <label>
        <input
          type="radio"
          value="option2"
          checked={selectedPasta === 'option2'}
          onChange={handleOptionChange}
        />
        Option 2
      </label>
      <label>
        <input
          type="radio"
          value="option3"
          checked={selectedPasta === 'option3'}
          onChange={handleOptionChange}
        />
        Option 3
      </label>
      <label>
        <input
          type="radio"
          value="option4"
          checked={selectedPasta === 'option4'}
          onChange={handleOptionChange}
        />
        Option 4
      </label>
    </div>
  )
}
