import React, { useState } from 'react'
import { Radio, RadioGroup } from 'react-custom-radio-buttons'
import { makeStyles } from '@material-ui/core/styles'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'
import Button from '@material-ui/core/Button'

const useStyles = makeStyles({
  App: {
    textAlign: 'center',
  },
  option: {
    width: '100px',
    height: '100px',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '5px',
  },
  optionsContainer: {
    display: 'flex',
    flexDirection: 'row',
  },
})

function getSteps() {
  return ['Recheio', 'Massa', 'Tamanho', 'Extra']
}
function getStepContent(step) {
  switch (step) {
    case 0:
      return 'Recheio'
    case 1:
      return 'Massa'
    case 2:
      return 'Tamanho'
    case 3:
      return 'Extra'
    default:
      return 'unknown step'
  }
}

const options = ['xs', 's', 'm', 'l', 'xl', 'xxl', 'xxxl']

function App() {
  const classes = useStyles()
  const [size, setSize] = useState(null)

  console.log(size)

  const onChange = (option) => {
    setSize(option)
  }

  const [activeStep, setActiveStep] = useState(0)
  const [selectedPasta, setSelectedPasta] = useState('')

  const steps = getSteps()

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1)
  }
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }
  const handleReset = () => {
    setActiveStep(0)
  }

  return (
    <div>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            All steps completed
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            {getStepContent(activeStep)}
            <div>
              <Button
                variant="contained"
                color="primary"
                disabled={activeStep === 0}
                onClick={handleBack}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>

      <div className={classes.App}>
        <h1>Please Selct Your Size</h1>

        <RadioGroup
          containerStyle={classes.optionsContainer}
          onChange={onChange}
        >
          {options.map((option, index) => (
            <Radio
              key={index}
              value={option}
              render={({ isSelected }) => (
                <button
                  className={classes.option}
                  style={{
                    backgroundColor: ` ${isSelected ? '#39ab31' : '#f9d10a'} `,
                  }}
                >
                  {option}
                </button>
              )}
            />
          ))}
        </RadioGroup>

        <h1>Your Size is {size}</h1>
      </div>
    </div>
  )
}

export default App
