import React, { useState } from 'react'
import { Radio, RadioGroup } from 'react-custom-radio-buttons'
import { makeStyles } from '@material-ui/core/styles'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'
import Button from '@material-ui/core/Button'
import Card from 'components/Card'

import recheios from 'services/recheios'
import massa from 'services/massa'
import tamanho from 'services/tamanho'
import extra from 'services/extra'

const useStyles = makeStyles({
  App: {
    textAlign: 'center',
  },
  option: {
    width: '100px',
    height: '100px',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '5px',
  },
  optionsContainer: {
    display: 'flex',

    flexWrap: 'wrap',

    justifyContent: 'space-between',
  },
})

const options = ['xs', 's', 'm', 'l', 'xl', 'xxl', 'xxxl']

function RadioComponent() {
  const classes = useStyles()
  const [size, setSize] = useState(null)

  console.log(size)

  const onChange = (option) => {
    setSize(option)
  }

  return (
    <div className={classes.App}>
      <h1 style={{ color: 'white' }}>Selecione o recheio</h1>
      <RadioGroup containerStyle={classes.optionsContainer} onChange={onChange}>
        {recheios.map(({ img, title, recommended }, index) => (
          <Radio
            key={index}
            value={title}
            render={({ isSelected }) => (
              <Card
                img={img}
                title={title}
                recommended={recommended}
                selected={isSelected}
              >
                {title}
              </Card>
            )}
          />
        ))}
      </RadioGroup>
      <h1 style={{ color: 'white' }}>Recheio selecionado: {size}</h1>
    </div>
  )
}

function getSteps() {
  return ['Recheio', 'Massa', 'Tamanho', 'Extra']
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return <RadioComponent />
    case 1:
      return <RadioComponent />
    case 2:
      return <RadioComponent />
    case 3:
      return <RadioComponent />
    default:
      return 'unknown step'
  }
}

function App() {
  const classes = useStyles()

  const [activeStep, setActiveStep] = useState(0)
  const [selectedPasta, setSelectedPasta] = useState('')

  const steps = getSteps()

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1)
  }
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }
  const handleReset = () => {
    setActiveStep(0)
  }

  return (
    <div>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            All steps completed
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            {getStepContent(activeStep)}
            <div>
              <Button
                variant="contained"
                color="primary"
                disabled={activeStep === 0}
                onClick={handleBack}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default App
