import React, { useState } from 'react'
import { Radio, RadioGroup } from 'react-custom-radio-buttons'
import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles({
  App: {
    textAlign: 'center',
  },
  option: {
    width: '50px',
    height: '50px',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '5px',
  },
  optionsContainer: {
    display: 'flex',
    flexDirection: 'row',
  },
})

const options = [
  { id: 1, value: 'option 1' },
  { id: 2, value: 'option 2' },
  { id: 3, value: 'option 3' },
  { id: 4, value: 'option 4' },
]

function App() {
  const classes = useStyles()
  const [size, setSize] = useState(null)

  const onChange = (option) => {
    setSize(option)
  }

  return (
    <div className="App">
      <h1>Please Selct Your Size</h1>
      <RadioGroup containerStyle="optionsContainer" onChange={onChange}>
        {options.map((id, option) => (
          <Radio
            value={option}
            key={id}
            render={({ isSelected }) => (
              <button
                className="option"
                style={{
                  backgroundColor: ` ${isSelected ? '#39ab31' : '#f9d10a'} `,
                }}
              >
                {option}
              </button>
            )}
          />
        ))}
      </RadioGroup>

      <h1>Your Size is {size}</h1>
    </div>
  )
}

export default App
