import React from 'react'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'

import { useStyles } from './styles'

export default function PurchaseStatus({ currentStatus }) {
  const classes = useStyles()

  function getSteps() {
    if (currentStatus === 'rejected_payment') {
      return ['awaiting_payment', 'rejected_payment']
    }

    if (currentStatus === 'expired_payment') {
      return ['awaiting_payment', 'expired_payment']
    }

    return ['awaiting_payment', 'successful_payment', 'successful_transfer']
  }

  function getActiveStep() {
    return getSteps().findIndex((el) => el === currentStatus) + 1
  }

  return (
    <Stepper
      activeStep={getActiveStep()}
      orientation="vertical"
      className={classes.Stepper}
      data-testid="stepper"
    >
      {getSteps().map((step, index) => {
        const labelProps = {}

        if (step === 'rejected_payment' && index === 1) {
          labelProps.error = true
        }
        return (
          <Step key={step} className={classes.Step}>
            <StepLabel {...labelProps}>
              {t(`components.cardOrder.statusCode.${step}`)}
            </StepLabel>
          </Step>
        )
      })}
    </Stepper>
  )
}
