import React, { useState } from 'react'
import { Radio, RadioGroup } from 'react-custom-radio-buttons'
import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles({
  App: {
    textAlign: 'center',
  },
  option: {
    width: '100px',
    height: '100px',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '5px',
  },
  optionsContainer: {
    display: 'flex',
    flexDirection: 'row',
  },
})

const options = ['xs', 's', 'm', 'l', 'xl', 'xxl', 'xxxl']

function App() {
  const classes = useStyles()
  const [size, setSize] = useState(null)

  console.log(size)

  const onChange = (option) => {
    setSize(option)
  }

  return (
    <div className={classes.App}>
      <h1>Please Selct Your Size</h1>
      <RadioGroup containerStyle={classes.optionsContainer} onChange={onChange}>
        {options.map((option, index) => (
          <Radio
            key={index}
            value={option}
            render={({ isSelected }) => (
              <button
                className={classes.option}
                style={{
                  backgroundColor: ` ${isSelected ? '#39ab31' : '#f9d10a'} `,
                }}
              >
                {option}
              </button>
            )}
          />
        ))}
      </RadioGroup>

      <h1>Your Size is {size}</h1>
    </div>
  )
}

export default App
